import React, {useState} from 'react';
import Axios from'axios';
import '../App.css'
import Doctor from '../components/doctorDashboard'
import Popup from '../popup'

import DateMomentUtils from '@date-io/moment';
import {DatePicker, TimePicker, MuiPickersUtilsProvider} from '@material-ui/pickers'
 

function Appointments() {

  const [isOpen, setIsOpen] = useState(false)
  

    const [list, setList]=useState([])


    const [date, setDate]=useState(new Date())


    const [appointment_date,setappointmentDate]=useState( )
    const [appointment_time,setpappointmentTime]=useState( )
    const [patient_id,setstatus]=useState( )
    
    const getData = () => {
        Axios.post('http://localhost:3001/appointments').then(res => {
            setList(res.data)
        })
    }
    
    getData()
  



    const togglePopup = () => {
      setIsOpen(!isOpen)
      console.log(isOpen)
    }




    const Postpone = ()=>{

togglePopup()

      return(
        <div className="popup-box" style={{display: 'flex',  justifyContent:'center', alignItems:'center', height: '100vh'}}>
            <div className="box">
                <span className="close-icon" onClick={togglePopup} style={{color:'white'}}>x</span>
                <div style={{position:'absolute', right:'50%', marginTop:'10px'}}>
        <b>Enter Data</b>
        </div>
        <div style={{ marginTop:'60px'}}>
        <MuiPickersUtilsProvider utils={DateMomentUtils} style = {{justifyContent:'center'}} >

    <div  >
    <DatePicker
          autoOk
          okLabel={false}
            label="Date"
            value={date}
            onChange={setDate}
            format='YYYY/MM/DD'
            minDate={new Date()}
            style={{left:'15%'}}/>

      </div>      
 
    <TimePicker 
    autoOk 
    okLabel={false}
    ampm={false}
    label="Time" 
    value={date} 
    onChange={setDate} 
    style={{left:'55%', bottom:'47.5px'}}/>
    

        </MuiPickersUtilsProvider>
        
        </div>
            </div>
            <div>
            
            </div>

        </div>
      )
    
    }
    





    return(
      <div >
        <Doctor/>
        <div className='appointments' style={{marginLeft: '100px', height:'20vh'}}>
        <h1>Your Appointments</h1>
        </div>  
        <table id = 'meetings' style={{alignItems: 'center',
            justifyContent: 'center', display: 'flex', height:'90vh', marginLeft: '160px',
            borderCollapse: 'separate', borderSpacing: '0 15px'}}>
      {/* <thead>
      <th>userName</th><th>password</th><th>status</th><th>&nbsp;</th>
      </thead> */}
      <tbody>
      {list.map((val,key)=>{
        return(
          <tr style={{boxShadow: '10px 10px 5px grey', backgroundColor: '#c4d8e2',
          border:'5px', borderSpacing: '0 15px', width: '150px', textAlign:'center'}}>
            
            
            
            <td style={{width:'50%'}}>
                {new Date(new Date().setDate(new Date(val.appointment_date).getDate())).toJSON().split('T')[0]}
              </td>


              <td>
                {(val.appointment_time).split(':').slice(0,-1).join(':')}
              </td>


            <td>{val.patient_id}
            </td>
            
            
            <td>

            <div className="button">
                  <button className="btn btn-primary btn-block" type="button" onClick={togglePopup} style={{ backgroundColor: '#0072c6', color: 'white'}}>Postpone</button>
                </div> 
            </td>
            
            
          </tr>



        )
      })}
      </tbody>
    </table>
    


    {isOpen && <Popup
      content={<>
      <div style={{position:'absolute', right:'50%', marginTop:'10px'}}>
        <b>Enter Data</b>
        </div>
        <div style={{ marginTop:'60px'}}>
        <MuiPickersUtilsProvider utils={DateMomentUtils} style = {{justifyContent:'center'}} >

    <div  >
    <DatePicker
          autoOk
          okLabel={false}
            label="Date"
            value={date}
            onChange={setDate}
            format='YYYY/MM/DD'
            minDate={new Date()}
            style={{left:'15%'}}/>

      </div>      
 
    <TimePicker 
    autoOk 
    okLabel={false}
    ampm={false}
    label="Time" 
    value={date} 
    onChange={setDate} 
    style={{left:'55%', bottom:'47.5px'}}/>
    

        </MuiPickersUtilsProvider>
        
        </div>
      </>}
      handleClose={togglePopup}
    />}
        </div>
    )

}
export default Appointments;
