import React, {useState} from 'react';
import Axios from'axios';
import '../App.css'
import '../pages/patient.css'
import Administrator from '../components/adminDashboard'
import Popup from '../popup';

function Admin() {


    const[isOpenForInsert, setIsOpenForInsert] = useState(false)
    const[isOpenForUpdate, setIsOpenForUpdate] = useState(false)

    const[list, setList]=useState([])

    const [administrator_signin, setadministrator_signin]=useState('')
    const [name,setname]=useState('')
    const [contact,setcontact]=useState('')
    
  
  
  
    const getData = () => {
        Axios.post('http://localhost:3001/admindetails').then(res => {
            setList(res.data)
        })
    }
    
    getData()

  

  
  
    const delete_admin = (administrator_signin)=>{
      Axios.delete(`http://localhost:3001/delete_admin/${administrator_signin}`)
      console.log('here')
      getData();
  }





  const insertAdmin = ()=>{
    Axios.post('http://localhost:3001/insert_admin', {
      name:name, 
      contact:contact
    }).then(()=>{
      alert("Inserted")
    })
    console.log('here')

    InsertTogglePopup()

    getData();
  }



  const UpdateAdmin = ()=>{
    Axios.put('http://localhost:3001/update_admin', {
      name:name, 
      contact:contact,
      administrator_signin: administrator_signin
    }).then(()=>{
      alert("Updated")
    })
    console.log('here')

    UpdateTogglePopup()

    getData();


    setadministrator_signin('')
    setname('')
    setcontact('')
  }


  const cancel = ()=>{
    setadministrator_signin('')
    setname('')
    setcontact('')
  }



  const InsertTogglePopup = () => {
    setIsOpenForInsert(!isOpenForInsert)
    console.log(isOpenForInsert)
  }


  
  const UpdateTogglePopup = (administrator_signin, name, contact)=>{
    
    setadministrator_signin(administrator_signin)
    setname(name)
    setcontact(contact)

    setIsOpenForUpdate(!isOpenForUpdate)
    console.log(isOpenForUpdate)
  }





    return(
      <div >
        <Administrator/>
        <div className='patients' style={{marginLeft: '100px', height:'20vh'}}>
        <h1>List of Administrators</h1>
        </div>
        
        <div style={{marginTop:'70px'}}>
        <table id = 'patients' style={{alignItems: 'center', 
            justifyContent: 'center', display: 'flex', height:'20vh', marginLeft: '160px',
            borderCollapse: 'separate', borderSpacing: '0 15px'}}>
      <tbody>
        
        <tr>
        
        <div style={{height:'3vh', marginBottom:'40px'}}>
        
        <div className="button" style={{height:'4vh',position:'absolute', right:'26%', marginTop:'10px'}}>
        <input type="button" value="Create" onClick={InsertTogglePopup} style={{ backgroundColor: '#0072c6', width:'170%', height:'150%', color:'white',
          borderRadius:'20px'}}/>
        </div>

        </div>
        </tr>




      {list.map((val,key)=>{
        return(
          <tr style={{boxShadow: '10px 10px 5px grey', backgroundColor: '#c4d8e2',
          border:'5px', borderSpacing: '0 15px', width: '150px', textAlign:'center'}}>
            <td>{val.administrator_signin}
            </td>
            <td style={{width:'20%'}}>{val.name}
            </td>
            <td>{val.contact}
            </td>
            <td>
            <div className="button">
                  <button className="btn btn-primary btn-block" type="button" style={{ backgroundColor: '#0072c6', color: 'white'}}
                  onClick={()=>{delete_admin(val.administrator_signin)}}>Delete</button>
                </div>
            </td>
            <td>


            <div className="button">
                  <button className="btn btn-primary btn-block" type="button" style={{ backgroundColor: '#0072c6', color: 'white'}}
                  onClick={()=>{UpdateTogglePopup(val.administrator_signin, val.name, val.contact)}}>Update</button>
                </div>
            {/* <div className="button" >
        <input className='btn btn-primary btn-block' type="button" value="Create" onClick={UpdateTogglePopup} style={{ backgroundColor: '#0072c6', width:'170%', height:'150%', color:'white',
          borderRadius:'20px'}}/>
        </div> */}

            {/* <div className="button">
                  <button className="btn btn-primary btn-block" type="button"  onClickCapture={UpdateTogglePopup(val.administrator_signin, val.name, val.contact)} style={{ backgroundColor: '#0072c6', color: 'white'}}>Update</button>
                </div> */}
            </td>
          </tr>
        )
      })}
      </tbody>
    </table>
    </div>


    {isOpenForInsert && <Popup
      content={<>
      <div style={{position:'absolute', right:'50%', marginTop:'10px'}}>
        <b>Enter Data</b>
        </div>
        <form>
          <div style={{marginTop:'50px'}}>
        <label htmlFor="fname">Name:</label>
        <input type="text" id="name" name="name" className="form-control" onChange={(e)=>{setname(e.target.value)}}/><br />
        </div>
        <div style={{marginTop:'30px'}}>
        <label htmlFor="lname">Contact:</label>
        <input type="text" id="contact" name="contact" style={{position:'absolute', right:'65%'}} onChange={(e)=>{setcontact(e.target.value)}}/>
        </div>
        <button type='button' style={{ backgroundColor: '#0072c6', width:'15%', height:'150%', color:'white',
          borderRadius:'20px', marginTop:'50px'}} onClick={insertAdmin}>Save</button>
      </form>
      </>}
      handleClose={InsertTogglePopup}
    />}



    {isOpenForUpdate && <Popup
      content={<>
      <div style={{position:'absolute', right:'50%', marginTop:'10px'}}>
        <b>Enter Data</b>
        </div>
        <form>
          <div style={{marginTop:'50px'}}>
        <label htmlFor="fname">Name:</label>
        <input type="text" id="name" name="name" style={{position:'absolute', right:'65%'}} defaultValue={name} onChange={(e)=>{setname(e.target.value)}}/><br />
        </div>
        <div style={{marginTop:'30px'}}>
        <label htmlFor="lname">Contact:</label>
        <input type="text" id="contact" name="contact" style={{position:'absolute', right:'65%'}} defaultValue={contact} onChange={(e)=>{setcontact(e.target.value)}}/>
        </div>
        <button type='button' style={{ backgroundColor: '#0072c6', width:'15%', height:'150%', color:'white',
          borderRadius:'20px', marginTop:'50px'}} onClick={UpdateAdmin}>Save</button>
      </form>
      </>}
      handleClose={UpdateTogglePopup}
    />}


        </div>
    )

}
export default Admin;
