import React, {useState} from 'react';
import Axios from'axios';
import '../App.css'
import Moderator from '../components/moderator_dashboard'
import Popup from '../popup'
import DateMomentUtils from '@date-io/moment';
import {DatePicker, TimePicker, MuiPickersUtilsProvider} from '@material-ui/pickers'



function Set_Appointments(){


    const [render, setRender] = useState(true)

    const getDoctorID = ()=>{
      
        Axios.get('http://localhost:3001/Doctor_ID').then(res => {
                  
        // setList(res.data)
        
      
        var string = JSON.stringify(res.data)
            
        var json = JSON.parse(string)
      
      // alert(string)
        // alert(json.doctor_id);


        if(json.doctor_id === 0){
          setRender(false)
          window.location='/'
          alert("You Need To Sign In First!!!")
        }
        
        // else{
        //   setRender(true)
        // }
            })
      
      }
      
      getDoctorID()
      



      const [appointments_List, setAppointmentList] = useState([])


      const payment_approval = ()=>{

        Axios.get('http://localhost:3001/payment_approval').then(res => {
            
            setAppointmentList(res.data)

        })
      } 



      payment_approval()



      const approving_Appointment = (appointment_id)=>{
        
        Axios.put('http://localhost:3001/approving_Appointment', {
            appointment_id: appointment_id
        })

        payment_approval()
      
    }


    

  


    return(
        <div >

        {render && (
            <div>
                <Moderator/>


                <table id = 'meetings' style={{alignItems: 'center',
            justifyContent: 'center', display: 'flex', height:'90vh', marginLeft: '160px',
            borderCollapse: 'separate', borderSpacing: '0 15px'}}>
      {/* <thead>
      <th>userName</th><th>password</th><th>status</th><th>&nbsp;</th>
      </thead> */}
      <tbody>
      <tr>
  
  </tr>
      {appointments_List.map((val,key)=>{
        return(
          <tr style={{boxShadow: '10px 10px 5px grey', backgroundColor: '#c4d8e2',
          border:'5px', borderSpacing: '0 15px', width: '150px', textAlign:'center'}}>
            
            
            <td style={{width:'50%'}}>
                {val.patient_name}
            </td>
            
            <td style={{width:'50%'}}>
                {new Date(new Date(val.appointment_date).setDate(new Date(val.appointment_date).getDate() + 1)).toJSON().split('T')[0]}
              </td>


              <td>
                {(val.appointment_time).split(':').slice(0,-1).join(':')}
              </td>


              <td>

                <div className="button">
                <button className="btn btn-primary btn-block" type="button" onClick={()=>{approving_Appointment(val.appointment_id)}} style={{ backgroundColor: '#0072c6', color: 'white'}}>Approved</button>
                </div> 
            </td>

            
            
            
          </tr>



        )
      })}
      </tbody>
    </table>
    





            </div>
        )}




        </div>
    )


}


export default Set_Appointments
