import React, {useState} from 'react';
import Axios from'axios';
import '../App.css'
import Doctor from '../components/doctorDashboard'
import Popup from '../popup'
import DateMomentUtils from '@date-io/moment';
import {DatePicker, TimePicker, MuiPickersUtilsProvider} from '@material-ui/pickers'



function Appointments() {


  
  
  const [isOpen, setIsOpen] = useState(false)
  
  const [isOpenForInsert, setIsOpenForInsert] = useState(false)
  
  const [list, setList]=useState([])
  const [patientlist, setPatientList] = useState([])

  
  const [patient_id, setPatientID] =useState('')
    const [appointment_date, setAppointmentDate]=useState( )
    const [appointment_time, setAppointmentTime]=useState( )
    const [appointment_id, setAppointmentid]=useState( )
    



  
    
    
    
    const [render, setRender] = useState(true)

    const getDoctorID = ()=>{
      
        Axios.get('http://localhost:3001/Doctor_ID').then(res => {
                  
        // setList(res.data)
        
      
        var string = JSON.stringify(res.data)
            
        var json = JSON.parse(string)
      
      // alert(string)
        // alert(json.doctor_id);


        if(json.doctor_id === 0){
          setRender(false)
          window.location='/'
          alert("You Need To Sign In First!!!")
        }
        
        // else{
        //   setRender(true)
        // }
            })
      
      }
      
      getDoctorID()
      




      // const getPatientsData = ()=>{

      //   Axios.post('http://localhost:3001/patients').then((res)=>{
      //             setPatientList(res.data)
      //           })
      
      // }
      
      // getPatientsData();

      




      const getData = () => {
        Axios.post('http://localhost:3001/appointments').then(res => {
            setList(res.data)
        })

        Axios.post('http://localhost:3001/patients').then(res => {
            setPatientList(res.data)
        })

    }
    
    getData()
 

    
    const togglePopup = (appointment_id, appointment_date, appointment_time) => {


      setAppointmentid(appointment_id)
      setAppointmentDate(appointment_date)
      setAppointmentTime(new Date(appointment_date).toDateString() +" "+ appointment_time +" "+ new Date().toTimeString().split(' ')[1])

      setIsOpen(!isOpen)
      console.log(isOpen)

    }


const UpdateAppointment = (appointment_date, appointment_time)=>{
  
  // alert(new Date(new Date(new Date(appointment_date).setDate(new Date(appointment_date).getDate() + 1)).toISOString()).toJSON().split('T')[0])
  // alert(new Date(appointment_time).toTimeString().split(' ')[0])

  Axios.put('http://localhost:3001/update_apointment', {
    appointment_date: new Date(new Date(new Date(appointment_date).setDate(new Date(appointment_date).getDate() + 1)).toISOString()).toJSON().split('T')[0],
    appointment_time: new Date(appointment_time).toTimeString().split(' ')[0],
    appointment_id: appointment_id 
  })

  togglePopup()

  getData()

}




const InsertTogglePopup = () => {

  setAppointmentDate(new Date())
  setAppointmentTime(new Date())

  setIsOpenForInsert(!isOpenForInsert)
  console.log(isOpenForInsert)
}





const insertAppointment = ()=>{
alert(patient_id + "\n" + new Date(new Date(new Date(appointment_date).setDate(new Date(appointment_date).getDate() + 1)).toISOString()).toJSON().split('T')[0] + "\n" + appointment_time)

  Axios.post('http://localhost:3001/insert_appointment', {
 appointment_date: new Date(new Date(new Date(appointment_date).setDate(new Date(appointment_date).getDate() + 1)).toISOString()).toJSON().split('T')[0],
    appointment_time: new Date(appointment_time).toTimeString().split(' ')[0],
    patient_id: patient_id

        }).then(()=>{
            alert("Inserted")
          })
         
          console.log('here')

          InsertTogglePopup()
          
          getData();

}





    return(
      <div >
        {render && (
          <div>

<Doctor/>
        <div className='appointments' style={{marginLeft: '100px', height:'20vh'}}>
        <h1>Your Appointments</h1>
        </div>  
        <table id = 'meetings' style={{alignItems: 'center',
            justifyContent: 'center', display: 'flex', height:'90vh', marginLeft: '160px',
            borderCollapse: 'separate', borderSpacing: '0 15px'}}>
      {/* <thead>
      <th>userName</th><th>password</th><th>status</th><th>&nbsp;</th>
      </thead> */}
      <tbody>
      <tr>
  <div style={{height:'3vh', marginBottom:'40px'}}>
<div className="button" style={{height:'4vh',position:'absolute', right:'13%', marginTop:'10px'}}>
<input type="button" value="Create" onClick={InsertTogglePopup} style={{ backgroundColor: '#0072c6', width:'170%', height:'150%', color:'white',
  borderRadius:'20px'}}/>
</div>

</div>
  </tr>
      {list.map((val,key)=>{
        return(
          <tr style={{boxShadow: '10px 10px 5px grey', backgroundColor: '#c4d8e2',
          border:'5px', borderSpacing: '0 15px', width: '150px', textAlign:'center'}}>
            
            
            
            <td style={{width:'50%'}}>
                {new Date(new Date(val.appointment_date).setDate(new Date(val.appointment_date).getDate() + 1)).toJSON().split('T')[0]}
              </td>


              <td>
                {(val.appointment_time).split(':').slice(0,-1).join(':')}
              </td>


            <td>{val.patient_name}
            </td>
            
            
            <td>

            <div className="button">
                  <button className="btn btn-primary btn-block" type="button" onClick={()=>{togglePopup(val.appointment_id, val.appointment_date, val.appointment_time)}} style={{ backgroundColor: '#0072c6', color: 'white'}}>Postpone</button>
                </div> 
            </td>
            
            
          </tr>



        )
      })}
      </tbody>
    </table>
    



    {isOpen && <Popup
      content={<>
      <div style={{position:'absolute', right:'50%', marginTop:'10px'}}>
        <b>Enter Data</b>
        </div>
        <div style={{ marginTop:'60px'}}>
        <MuiPickersUtilsProvider utils={DateMomentUtils} style = {{justifyContent:'center'}} >

    <div  >
    <DatePicker
          autoOk
            label="Date"
            value={appointment_date}
            onChange={setAppointmentDate}
            format='YYYY/MM/DD'
            maxDate={new Date(new Date().getFullYear() + 1, new Date().getMonth(), new Date().getDate())}
            minDate={new Date()}
            style={{left:'15%'}}/>

      </div>      
 
    <TimePicker 
    autoOk 
    ampm={false}
    label="Time" 
    onChange={setAppointmentTime} 
    style={{left:'15%', marginTop:'25px'}}/>
    

        </MuiPickersUtilsProvider>
        
        </div>
        <button type="button" style={{ backgroundColor: '#0072c6', width:'15%', height:'150%', color:'white',
          borderRadius:'20px', marginTop:'50px'}} onClick={()=>{UpdateAppointment(appointment_date, appointment_time)}} >Save</button>
      </>}
      handleClose={togglePopup}
    />}




    {isOpenForInsert && <Popup
    content={<>
    <div style={{position:'absolute', right:'50%', marginTop:'10px'}}>
<b>Enter Data</b>
</div>
<form>
  <div style={{marginTop:'70px'}}>
  <div style={{ marginTop:'60px'}}>
        <MuiPickersUtilsProvider utils={DateMomentUtils} style = {{justifyContent:'center'}} >

    <div  >
    <DatePicker
          autoOk
            label="Date"
            value={appointment_date}
            onChange={setAppointmentDate}
            format='YYYY/MM/DD'
            maxDate={new Date(new Date().getFullYear() + 1, new Date().getMonth(), new Date().getDate())}
            minDate={new Date()}
            style={{left:'15%'}}/>

      </div>      
 
    <TimePicker 
    autoOk 
    ampm={false}
    label="Time" 
    value={appointment_time} 
    onChange={setAppointmentTime} 
    style={{left:'15%', marginTop:'25px'}}/>
    

        </MuiPickersUtilsProvider>
        
        </div>
<div>
        <select defaultValue="" onChange={(e)=>{setPatientID(e.target.value)}} style={{position:'absolute', right:'65%', width:'23%'}}>
        <option disabled="disabled" value="" >select an option</option>
        {patientlist.map((val,key)=>(
            <option value={val.patient_id}>{val.patient_name}</option>
        ))}

        </select>
</div>
</div>

<button type="button" style={{ backgroundColor: '#0072c6', width:'15%', height:'150%', color:'white',
  borderRadius:'20px', marginTop:'50px'}} onClick={insertAppointment}>Save</button>
</form>
    </>}
    handleClose = {InsertTogglePopup}
    />}

          </div>
        )}
        
        </div>
    )

}


export default Appointments;
