import React, {useEffect, useRef, useState} from 'react';
import Axios from'axios';
import '../App.css'
import Doctor from '../components/doctorDashboard'
import Button from "@material-ui/core/Button"
import { IconButton } from '@material-ui/core/IconButton';
import { TextField } from '@material-ui/core/TextField';
import AssignmentIcon from "@material-ui/icons/Assignment"
import PhoneIcon from "@material-ui/icons/Phone"
import Peer from "simple-peer"
import io from "socket.io-client"



const socket = io.connect("http://localhost:3001/video_call")



function VideoCall() {


const [me, setMe] = useState("")
const [stream, setStream] = useState()
const [receivingCall, setReceivingCall] = useState(false)
const [caller, setCaller] = useState("")
const [callerSignal, setCallerSignal] = useState()
const [callerAccepted, setCallAccepted] = useState(false)
const [idToCall, setIdToCall] = useState("")
const [callEnded, setCallEnded] = useState(false)
const [name, setName] = useState("")


const myVideo = useRef()
const userVideo = useRef()
const connectionRef = useRef()  // used for ending the call


useEffect(()=>{
    navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
    }).then((stream)=>{
        setStream(stream)
        myVideo.current.srcObject = stream

    })

    socket.on('me', (id)=>{
        setMe(id)
    })


    
    socket.on('callUser', (data)=>{
        setReceivingCall(true)
        setCaller(data.from)
        setName(data.name)
        setCallerSignal(data.signal)
    })

}, [])

// ablwe to call the user
const callUser = (id)=>{
    const peer = new Peer({
        initiator: true,
        trickle: false,
        stream: stream
    })

    peer.on("signal", (data)=>{
        socket.emit("callUser", {
            userToCall: id,
            signalData: data,
            from: me,
            name:name
        })
    })


    peer.on("stream", (stream)=>{
        userVideo.current.srcObject = stream
    })


    socket.on("callAccepted", (signal)=>{
        setCallAccepted(true)
        peer.signal(signal)
    })

    connectionRef.current = peer

}




const answerCall = ()=>{
    setCallAccepted(true)
    
    
    
    const peer = new Peer({
        initiator: false,
        trickle: false,
        stream: stream
    })


    peer.on("signal", (data)=>{
        socket.emit("answerCall", {
            signal: data,
            to: caller
        })
    })


    
    peer.on("stream", (stream)=>{
        userVideo.current.srcObject = stream
    })


    
    peer.signal(callerSignal)

    connectionRef.current = peer

}
    


const leaveCall = ()=>{
    setCallEnded(true)

    connectionRef.current.destroy()
}


    const[list, setList]=useState([])
    const [patient_name,setuserName]=useState('')
    const [patient_gender,setpassword]=useState('')
    const [patient_age,setstatus]=useState( )
    


        
    const [render, setRender] = useState(true)

    const getDoctorID = ()=>{
      
        Axios.get('http://localhost:3001/Doctor_ID').then(res => {
                  
        // setList(res.data)
        
      
        var string = JSON.stringify(res.data)
            
        var json = JSON.parse(string)
      
      // alert(string)
        // alert(json.doctor_id);


        if(json.doctor_id === 0){
          setRender(false)
          window.location='/'
          alert("You Need To Sign In First!!!")
        }
        
        // else{
        //   setRender(true)
        // }
            })
      
      }
      
      getDoctorID()
      


return(

    
    <div>
    {render && (
      <div>
      <Doctor/>
  
      </div>

    )}
  </div>
  

)


}

export default VideoCall
