import React, {useState} from 'react';
import Axios from 'axios';
import '../App.css'
import Popup from '../popup'
import Doctor from '../components/doctorDashboard'
import { useParams } from 'react-router-dom';



function Profile() {

  
 // const[list, setList]=useState([])
  
  
  const [name,setName]=useState(useParams().name)
  const [password,setPassword]=useState(useParams().password)
  const [designation,setDesignation]=useState(useParams().designation)
  const [gender,setGender]=useState(useParams().gender)
  const [address,setAddress]=useState(useParams().address)
  const [highest_qualification,setHighest_Qualification]=useState(useParams().highest_qualification)





    
    
  const [render, setRender] = useState(true)

  const getDoctorID = ()=>{
    
      Axios.get('http://localhost:3001/Doctor_ID').then(res => {
                
      // setList(res.data)
      
    
      var string = JSON.stringify(res.data)
          
      var json = JSON.parse(string)
    
    // alert(string)
      // alert(json.doctor_id);


      if(json.doctor_id === 0){
        setRender(false)
        window.location='/'
        alert("You Need To Sign In First!!!")
      }
      
      // else{
      //   setRender(true)
      // }
          })
    
    }
    
    getDoctorID()
    






  const UpdateProfile = ()=>{
    
    alert("name : "+name +"\n password : "+ password + "\ndesignation : " + designation + "\ngender : "+ gender + "\naddress : " + address + "\nhighest_Qualification : " + highest_qualification)

    
    
    Axios.put("http://localhost:3001/update_profile", {

      name: name,
      password: password,
      designation: designation,
      gender: gender,
      address: address,
      highest_qualification: highest_qualification
    
    })

  }
  











    return(
    <div>
      {render && (

<div>
<Doctor/>

<div className='profile'>
<meta charset="utf-8"/>
<meta name="viewport" content="width=device-width, initial-scale=1.0"/>
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/twitter-bootstrap/4.1.0/css/bootstrap.min.css"/>
<link rel="stylesheet" href="assets/css/styles.min.css"></link>

{/* <h1>Your Profile</h1> */}



<div className="form-container" style={{height:'10vh', width:'40%', margin:'0', }}>
  <form  >
    <h2 className="text-center"><strong>Account Details</strong></h2>
   <table style={{cellSpacing:'0', padding:'0', backgroundColor: '#c4d8e2'}}>
     
     
           
     
     <tbody style={{boxShadow: '10px 10px 5px grey', 
     border:'0px', borderSpacing: '0px', width: '150px', textAlign:'center', borderCollapse:'collapse'}}>
        
    
    <tr>
    
    <td>
    <label style={{fontSize:'20px', width:'10%'}} >Name:</label>
    </td>
    <td>

    <input className="form-control" type="text" name="email"  defaultValue={name}  onChange={(e)=>{setName(e.target.value)}} ></input>
    </td>
    </tr>
    
    
    <tr>
    
    <td>
  <label style={{fontSize:'20px'}} >Password:</label>
  </td>
  <td style={{width:'100%'}}>
  <input className="form-control" type="password" name="email"  onChange={(e)=>{setPassword(e.target.value)}} defaultValue={password}></input>
  </td>
  
  </tr>
  
  <tr>
    
    <td>
  <label style={{fontSize:'20px'}} >Designation:</label>
  </td>
  <td style={{width:'100%'}}>
  <input className="form-control" type="name" name="email" onChange={(e)=>{setDesignation(e.target.value)}} defaultValue={designation} ></input>
  </td>
  
  </tr>
  <tr>
    
    <td>
  <label style={{fontSize:'20px'}} >Gender:</label>
  </td>
  <td style={{width:'100%'}}>
  <input className="form-control" type="name" name="email" onChange={(e)=>{setGender(e.target.value)}} defaultValue={gender}></input>
  </td>
  
  </tr>
  <tr>
    
    <td>
  <label style={{fontSize:'20px'}} >Address:</label>
  </td>
  <td style={{width:'100%'}}>
  <input className="form-control" type="name" name="email" onChange={(e)=>{setAddress(e.target.value)}} defaultValue={address}></input>
  </td>
  
  </tr>
  <tr>
    
    <td>
  <label style={{fontSize:'20px'}} >Highest Qualification:</label>
  </td>
  <td style={{width:'100%'}}>
  <input className="form-control" type="name" name="email" onChange={(e)=>{setHighest_Qualification(e.target.value)}} defaultValue={highest_qualification}></input>
  </td>
  
  </tr>
    
    </tbody>
    
  </table>
  
    <div className="form-group">
      <button className="btn btn-primary btn-block" type='button' style={{marginTop:'80px' }}  onClick={UpdateProfile} >Edit</button>
      </div>
  </form>
</div>

</div>
<script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.2.1/jquery.min.js"></script>
<script src="https://cdnjs.cloudflare.com/ajax/libs/twitter-bootstrap/4.1.0/js/bootstrap.bundle.min.js"></script>

</div>
        
      )}
    </div>
    

    )

}
export default Profile;
