import React, {useState} from 'react';
import Axios from'axios';
import '../App.css'
import Administrator from '../components/adminDashboard';
import Popup from '../popup';



function Doctors() {

    const[isOpenForInsert, setIsOpenForInsert] = useState(false)

    const[isOpenForUpdate, setIsOpenForUpdate] = useState(false)
    
    const [doctor_list, setDoctorList] = useState([])


    const [doctor_id, setdoctorid] = useState('')
    const [name, setName] = useState('')
    const [designation, setDesignation] = useState('')
    const [gender, setGender] = useState('')
    const [address, setAddress] = useState('')
    const [highest_qualification, setHighest_Qualification] = useState('')



const [nameError, setNameError] = useState('')
    const [designationError, setDesignationError] = useState('')
    const [genderError, setGenderError] = useState('')
    const [addressError, setAddressError] = useState('')
    const [highest_qualificationError, setHighest_QualificationError] = useState('')



    const [render, setRender] = useState(true)

    const getAdminID = ()=>{
      
        Axios.get('http://localhost:3001/Admin_ID').then(res => {
                  
        // setList(res.data)
        
      
        var string = JSON.stringify(res.data)
            
        var json = JSON.parse(string)
      
      // alert(string)
        // alert(json.doctor_id);


        if(json.admin_id === 0){
          setRender(false)
          window.location='/'
          alert("You Need To Sign In First!!!")
        }
        
        // else{
        //   setRender(true)
        // }
            })
      
      }
      
      getAdminID()
      





    const getData = ()=>{
        Axios.post('http://localhost:3001/doctors_list').then((res)=>{
            setDoctorList(res.data)
        })
    }


    getData();

    const delete_doctor = (doctor_id)=>{
        Axios.delete(`http://localhost:3001/delete_doctor/${doctor_id}`)
        console.log('here')
        getData();
    }

  


    const insertDoctor= ()=>{

      if(name==''){
        document.getElementById('name').parentElement.className = 'form-class error'
   
      setNameError("Please enter the name")

      }

      if(designation == ''){
        document.getElementById('designation').parentElement.className = 'form-class error'
   
      setDesignationError("Please enter the designation")

      }

      if(gender=='' ){
        document.getElementById('gender').parentElement.className = 'form-class error'
   
      setGenderError("Please enter the gender")

      }

      if(address==''){
        document.getElementById('address').parentElement.className = 'form-class error'
   
        setAddressError("Please enter the address")
      }

      if(highest_qualification==''){
        document.getElementById('highest_qualification').parentElement.className = 'form-class error'
   
        setHighest_QualificationError("Please enter the highest qualification")
      }





      if(name!=''){
        document.getElementById('name').parentElement.className = 'form-class error'
   
      setNameError("")

      }

      if(designation != ''){
        document.getElementById('designation').parentElement.className = 'form-class error'
   
      setDesignationError("")

      }

      if(gender!='' ){
        document.getElementById('gender').parentElement.className = 'form-class error'
   
      setGenderError("")

      }

      if(address!=''){
        document.getElementById('address').parentElement.className = 'form-class error'
   
        setAddressError("")
      }

      if(highest_qualification!=''){
        document.getElementById('highest_qualification').parentElement.className = 'form-class error'
   
        setHighest_QualificationError("")
      }

      


      if(name != '' && designation != '' && gender!='' && address!='' && highest_qualification!=''){
        
        Axios.post('http://localhost:3001/insert_doctor', {
            name: name,
            designation: designation,
            gender: gender,
            address: address,
            highest_qualification: highest_qualification,
        }).then(()=>{
            alert("Inserted")
          })
          console.log('here')

          InsertTogglePopup()
          
          setName('')
          setGender('')
          setAddress('')
          setDesignation('')
          setHighest_Qualification('')

          getData();

      }
        

        
    }

    
    const UpdateDoctor = ()=>{
      
      if(name==''){
        document.getElementById('name').parentElement.className = 'form-class error'
   
      setNameError("Please enter the name")

      }

      if(designation == ''){
        document.getElementById('designation').parentElement.className = 'form-class error'
   
      setDesignationError("Please enter the designation")

      }

      if(gender=='' ){
        document.getElementById('gender').parentElement.className = 'form-class error'
   
      setGenderError("Please enter the gender")

      }

      if(address==''){
        document.getElementById('address').parentElement.className = 'form-class error'
   
        setAddressError("Please enter the address")
      }

      if(highest_qualification==''){
        document.getElementById('highest_qualification').parentElement.className = 'form-class error'
   
        setHighest_QualificationError("Please enter the highest qualification")
      }





      if(name!=''){
        document.getElementById('name').parentElement.className = 'form-class error'
   
      setNameError("")

      }

      if(designation != ''){
        document.getElementById('designation').parentElement.className = 'form-class error'
   
      setDesignationError("")

      }

      if(gender!='' ){
        document.getElementById('gender').parentElement.className = 'form-class error'
   
      setGenderError("")

      }

      if(address!=''){
        document.getElementById('address').parentElement.className = 'form-class error'
   
        setAddressError("")
      }

      if(highest_qualification!=''){
        document.getElementById('highest_qualification').parentElement.className = 'form-class error'
   
        setHighest_QualificationError("")
      }

      


      if(name != '' && designation != '' && gender!='' && address!='' && highest_qualification!=''){

        Axios.put('http://localhost:3001/update_doctors_list', {
            name: name,
            designation: designation,
            gender: gender,
            address: address,
            highest_qualification: highest_qualification,
            doctor_id: doctor_id
        })

        UpdateTogglePopup()


        setName('')
        setGender('')
        setAddress('')
        setDesignation('')
        setHighest_Qualification('')


        getData();


      }

      

        
    }



    const InsertTogglePopup = () => {

      setNameError('')
          setGenderError('')
          setAddressError('')
          setDesignationError('')
          setHighest_QualificationError('')

      setIsOpenForInsert(!isOpenForInsert)
      console.log(isOpenForInsert)
    }


    const UpdateTogglePopup = (doctor_id, name, designation, gender, address, highest_qualification) => {

      setNameError('')
          setGenderError('')
          setAddressError('')
          setDesignationError('')
          setHighest_QualificationError('')
          
      setdoctorid(doctor_id)
      setName(name)
      setDesignation(designation)
      setGender(gender)
      setAddress(address)
      setHighest_Qualification(highest_qualification)


      setIsOpenForUpdate(!isOpenForUpdate)
      console.log(isOpenForUpdate)
    }



    return(
      <div>
        {render && (

<div >
<Administrator/>
<div className='patients' style={{marginLeft: '100px', height:'20vh'}}>
<h1>List of Doctors</h1>
</div>  
<div style={{marginTop:'20vh'}}>
<table id = 'patients' style={{alignItems: 'center',
    justifyContent: 'center', display: 'flex', height:'20vh', marginLeft: '160px',
    borderCollapse: 'separate', borderSpacing: '0 15px'}}>
<tbody>
<tr>
  <div style={{height:'3vh', marginBottom:'40px'}}>
<div className="button" style={{height:'4vh',position:'absolute', right:'13%', marginTop:'10px'}}>
<input type="button" value="Create" onClick={InsertTogglePopup} style={{ backgroundColor: '#0072c6', width:'170%', height:'150%', color:'white',
  borderRadius:'20px'}}/>
</div>

</div>
  </tr>
{doctor_list.map((val,key)=>{
return(
  <tr style={{boxShadow: '10px 10px 5px grey', backgroundColor: '#c4d8e2',
  border:'5px', borderSpacing: '0 15px', width: '150px', textAlign:'center'}}>
    <td>{val.name}
    </td>
    <td>{val.designation}
    </td>
    <td>{val.gender}
    </td>
    <td style={{width:'20%'}}>{val.address}
    </td>
    <td>{val.highest_qualification}
    </td>
    <td>
    <div className="button">
          <button className="btn btn-primary btn-block" type="button" style={{ backgroundColor: '#0072c6', color: 'white'}}
          onClick={()=>{delete_doctor(val.doctor_id)}}>Delete</button>
        </div>
    </td>
    <td>
    <div className="button">
          <button className="btn btn-primary btn-block" type="button" onClick={()=>{UpdateTogglePopup(val.doctor_id, val.name, val.designation, val.gender, val.address, val.highest_qualification)}} style={{ backgroundColor: '#0072c6', color: 'white'}}>Update</button>
        </div>
    </td>
  </tr>
)
})}
</tbody>
</table>
</div>


{isOpenForInsert && <Popup
content={<>
  <div style={{position:'absolute', right:'50%', marginTop:'10px'}}>
    <b>Enter Data</b>
    </div>
    <form>
      <div style={{marginTop:'70px'}}>
    <div>
    <label  htmlFor="fname">Name:</label>
    <div className="form-class"  style={{position:'absolute', right:'65%', left:'2.5%', width:'40%'}}>
    <input type="text" id="name" name="name" className='form-control' style={{position:'absolute', right:'65%', width:'100%', height:'100%', left:'0%'}} onChange={(e)=>{setName(e.target.value)}}/>
    </div>
    <p className='error' style={{position:'absolute', right:'65%', marginTop:'50px', left:'5'}}>{nameError}</p>
    </div>
    
    <div>

    <label htmlFor="fname" style={{position:'absolute', right:'25%', top:'90px'}}>Designation:</label>
    <div className='form-class' style={{position:'absolute', right:'10%', width:'40%'}}> 
     <input type="text" className='form-control' id="designation" name="name" style={{position:'absolute', right:'27%', width:'100%', height:'100%', left:'0%'}} onChange={(e)=>{setDesignation(e.target.value)}}/><br /> 
     </div>
    <p className='error' style={{position:'absolute', right:'4%', marginTop:'50px'}}>{designationError}</p>
    </div>
    
    </div>

    <div style={{marginTop:'30px'}}>
<div>  
    <label htmlFor="lname">Gender:</label>
    <div className='form-class' style={{position:'absolute', right:'65%', width:'40%' }}>
    <select className='form-control' id='gender' onChange={(e)=>{setGender(e.target.value)}} style={{position:'absolute', right:'65%', width:'100%', height:'100%', left:'0%' }}>
    <option >select an option</option>
    <option value="M" >M</option>
    <option value="F">F</option>
  </select>
  </div>
  <p className='error' style={{position:'absolute', right:'65%', marginTop:'50px'}}>{genderError}</p>
  </div>
  <label htmlFor="fname" style={{position:'absolute', right:'55%'}}>Address:</label>
    <input type="text" id="address" name="name" style={{position:'absolute', right:'27%'}} onChange={(e)=>{setAddress(e.target.value)}}/>
    </div>
    <div style={{marginTop:'30px'}}>
    <label htmlFor="fname" style={{position:'absolute', right:'60%'}}>Highest Qualification:</label>
    <input type="text" id="highest_qualification" name="name" style={{position:'absolute', right:'35%'}} onChange={(e)=>{setHighest_Qualification(e.target.value)}}/><br />
    </div>
    <button type='button' style={{ backgroundColor: '#0072c6', width:'15%', height:'150%', color:'white',
      borderRadius:'20px', marginTop:'50px'}} onClick={insertDoctor} >Save</button>
  </form>
  </>}
handleClose={InsertTogglePopup}
/>}




{isOpenForUpdate && <Popup
content={<>
<div style={{position:'absolute', right:'50%', marginTop:'10px'}}>
<b>Enter Data</b>
</div>
<form>
  <div style={{marginTop:'70px'}}>
<label htmlFor="fname">Name:</label>

<input type="text" id="name" name="name" style={{position:'absolute', right:'65%'}} defaultValue={name} onChange={(e)=>{setName(e.target.value)}}/>


<label htmlFor="fname" style={{position:'absolute', right:'51%'}}>Designation:</label>

<input type="text" id="name" name="name" style={{position:'absolute', right:'27%'}} defaultValue={designation} onChange={(e)=>{setDesignation(e.target.value)}}/><br />
</div>



<div style={{marginTop:'30px'}}>
<label htmlFor="lname">Gender:</label>
<select defaultValue={gender} onChange={(e)=>{setGender(e.target.value)}} style={{position:'absolute', right:'65%', width:'23%'}}>
<option >select an option</option>
<option value="M" >M</option>
<option value="F">F</option>
</select>



<label htmlFor="fname" style={{position:'absolute', right:'55%'}}>Address:</label>
<input type="text" id="name" name="name" style={{position:'absolute', right:'27%'}} defaultValue={address} onChange={(e)=>{setAddress(e.target.value)}}/>
</div>


<div style={{marginTop:'30px'}}>
<label htmlFor="fname" style={{position:'absolute', right:'60%'}}>Highest Qualification:</label>
<input type="text" id="name" name="name" style={{position:'absolute', right:'35%'}} defaultValue={highest_qualification} onChange={(e)=>{setHighest_Qualification(e.target.value)}}/><br />
</div>

<button type="button" style={{ backgroundColor: '#0072c6', width:'15%', height:'150%', color:'white',
  borderRadius:'20px', marginTop:'50px'}} onClick={UpdateDoctor}>Save</button>
</form>
</>}
handleClose={UpdateTogglePopup}
/>}




</div>


        )}
      </div>
      
      
    


    )
}



export default Doctors;
