import React, {useState, useEffect} from 'react';
import { Navigate, useNavigate } from 'react-router-dom'
import Axios from'axios';
import './App.css';
import Head from './partials/head'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faCheckCircle, faExclamationCircle} from '@fortawesome/free-solid-svg-icons'

function SignIn() {
  

  const[email, setEmail] = useState("");
  const[password, setPassword] = useState("");

  const [loginStatus, setLoginStatus] = useState("")


Axios.defaults.withCredentials = true;


let navigate = useNavigate();


const Login = ()=>{

  Axios.post('http://localhost:3001/login', {

  email: email,
    password: password

  }).then((res)=>{
    
    console.log("test")
    
      var string = JSON.stringify(res.data)
      
      var json = JSON.parse(string)


      alert(json[0].User);


    
      if(json[0].User === "admin"){
        // alert("Valid"+loginStatus)
        // console.log('here now')
        navigate('/doctors_list')
      }
      else if(json[0].User === "doctor"){
        navigate('/meetings')
      }
      else{
        alert("Incorrect Email Address or Password.\nPlease provide correct information")
      }


    });
  };

  useEffect(() => {
    Axios.get('http://localhost:3001/login').then((response)=>{
      if(response.data.loggedIn == true){
        setLoginStatus(response.data.user[0])
      }  
      console.log(response.data.user)
    })
  }, [])




  const form = document.getElementById('form');
  const email_Input_Box = document.getElementById('emailInput');
  const password_Input_Box = document.getElementById('passwordInput');



const Check = ()=>{

  form.addEventListener('submit' , (e)=>{

    checkInputs();
    
  })


}

  




  const checkInputs = ()=>{
alert(email)
    if(email === ''){
      setErrorFor(email_Input_Box, 'Please enter the email address')
    }
    else{
      // setSuccessFor(email_Input_Box)
    }

  }


  const setErrorFor = (input, Error_Message)=>{

    const formGroup = input.parentElement;

    const text = formGroup.querySelector('text')

    text.innerText = Error_Message

    const form_control = formGroup.querySelector('form-control')

    form_control.className = 'form-control error'


  }






    return(
      <div>
        <Head/>
        
          <div>
            <nav className="navbar navbar-light navbar-expand-md navigation-clean" style={{backgroundColor: '#0072c6'}}>
              <div className="container">
              <img src='https://icon-library.com/images/healthcare-icon/healthcare-icon-7.jpg' style={{height:'80px', width:'80px', position:'absolute', right:'88%'}}/>
                <a className="navbar-brand" href="#" style={{color: '#c4d8e2', textAlign: 'center'}}>Tele Healthcare</a>
              </div>
            </nav>
          </div>
          <div className="register-photo">
          
            <div className="form-container">
            <div className='image-holder' ></div>
              <form id='form' >
                <h2 className="text-center">
                  <strong>Sign In to your account.</strong>
                </h2>
                <div className="form-group" >
                  <div className="form-class error" style={{height:'45px', width:'100%', borderRadius:'4px'}}>
                  <input style={{marginBottom:'30px'}} className="form-control" id='emailInput' name="email" placeholder="Email" onChange={(e)=>{setEmail(e.target.value)}}/>
                  </div>
                  <div className="CheckCircle">
                  <FontAwesomeIcon icon ={faCheckCircle}  ></FontAwesomeIcon>
                  </div>
                  <div className="ExclamationCircle">
                  <FontAwesomeIcon icon ={faExclamationCircle}  ></FontAwesomeIcon>
                  </div>
                  <text className="Message">Error Message</text>
                </div>
                <div className="form-group" >
                  <input style={{marginBottom:'30px'}} className="form-control" id='passwordInput' type="password" name="password" placeholder="Password" onChange={(e)=>{setPassword(e.target.value)}} />
                  <div className="CheckCircle">
                  <FontAwesomeIcon icon ={faCheckCircle}  ></FontAwesomeIcon>
                  </div>
                  <div className="ExclamationCircle">
                  <FontAwesomeIcon icon ={faExclamationCircle}  ></FontAwesomeIcon>
                  </div>
                  <text className="Message">Error Message</text>
                </div>
                <div className="button">
                  <button className="btn btn-primary btn-block" type="submit" onClick={Check} style={{ backgroundColor: '#0072c6', color: '#82c6e2'}}>Sign In</button>
                </div>
              </form>
            </div>
            
          </div>
        </div>
    )

}
export default SignIn;
