import React, {useState} from 'react';
import Axios from'axios';
import '../App.css'
import Doctor from '../components/doctorDashboard'




function Profile() {

  const[list, setList]=useState([])
    const [name,setName]=useState('')
    const [password,setPassword]=useState('')
    const [designation,setDesignation]=useState('')
    const [gender,setGender]=useState('')
    const [address,setAddress]=useState('')
    const [highest_qualification,setHighest_Qualification]=useState('')




    
    const getData = () => {
      Axios.post('http://localhost:3001/profile').then(res => {

      setList(res.data)

      })
  }
  

  getData();

  


  // const [name,setName]=useState('')
  // const [password,setPassword]=useState('')
  // const [designation,setDesignation]=useState('')
  // const [gender,setGender]=useState('')
  // const [address,setAddress]=useState('')
  // const [highest_qualification,setHighest_Qualification]=useState('')



  // list.map((val, key)=>{
  //   setName(val.name)
  //   setPassword(val.password)
  //   setDesignation(val.designation)
  //   setGender(val.gender)
  //   setAddress(val.address)
  //   setHighest_Qualification(val.highest_qualification)
  // })




  const updateProfile = (name, password, designation, gender, address, highest_qualification) =>{
    Axios.put("http://localhost:3001/update_profile", {
      name:name,
      password:password,
      designation:designation,
      gender:gender,
      address:address,
      highest_qualification:highest_qualification
    })
  }
  





const Update = ()=>{
  alert(name +"\n"+ password + "\n" + designation + "\n"+ gender + "\n" + address + "\n" + highest_qualification)

}






    return(
      <div>


        <Doctor/>

      <div className='profile'>
        <meta charset="utf-8"/>
        <meta name="viewport" content="width=device-width, initial-scale=1.0"/>
        <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/twitter-bootstrap/4.1.0/css/bootstrap.min.css"/>
        <link rel="stylesheet" href="assets/css/styles.min.css"></link>
        
        {/* <h1>Your Profile</h1> */}

      
        
        <div className="form-container" style={{height:'10vh', width:'40%', margin:'0', }}>
          <form  >
            <h2 className="text-center"><strong>Account Details</strong></h2>
           <table style={{cellSpacing:'0', padding:'0', backgroundColor: '#c4d8e2'}}>
             {list.map((val,key)=>{
                     return(
             
                   
             
             <tbody style={{boxShadow: '10px 10px 5px grey', 
             border:'0px', borderSpacing: '0px', width: '150px', textAlign:'center', borderCollapse:'collapse'}}>
                
            
            <tr>
            
            <td>
            <label style={{fontSize:'20px', width:'10%'}} >Name:</label>
            </td>
            <td>
            <input className="form-control" type="text" name="email" value={name}  onChange={(e)=>{setName(e.target.value)}} />
            </td>
            </tr>
            
            
            <tr>
            
            <td>
          <label style={{fontSize:'20px'}} >Password:</label>
          </td>
          <td style={{width:'100%'}}>
          <input className="form-control" type="password" name="email" onChange={(e)=>{setPassword(e.target.value)}} value={val.password}/>
          </td>
          
          </tr>
          
          <tr>
            
            <td>
          <label style={{fontSize:'20px'}} >Designation:</label>
          </td>
          <td style={{width:'100%'}}>
          <input className="form-control" type="name" name="email" onChange={(e)=>{setDesignation(e.target.value)}} value={val.designation} />
          </td>
          
          </tr>
          <tr>
            
            <td>
          <label style={{fontSize:'20px'}} >Gender:</label>
          </td>
          <td style={{width:'100%'}}>
          <input className="form-control" type="name" name="email" onChange={(e)=>{setGender(e.target.value)}} value={val.gender}/>
          </td>
          
          </tr>
          <tr>
            
            <td>
          <label style={{fontSize:'20px'}} >Address:</label>
          </td>
          <td style={{width:'100%'}}>
          <input className="form-control" type="name" name="email" onChange={(e)=>{setAddress(e.target.value)}} value={val.address}/>
          </td>
          
          </tr>
          <tr>
            
            <td>
          <label style={{fontSize:'20px'}} >Highest Qualification:</label>
          </td>
          <td style={{width:'100%'}}>
          <input className="form-control" type="name" name="email" onChange={(e)=>{setHighest_Qualification(e.target.value)}} value={val.highest_qualification}/>
          </td>
          
          </tr>
            {/* <div className="form-group"><input className="form-control"  name="password" placeholder="Password" /></div>
            <div className="form-group"><input className="form-control"  name="password-repeat" placeholder="Designation" /></div>
            <div className="form-group"><input className="form-control"  name="password-repeat" placeholder="Gender" /></div>
            <div className="form-group"><input className="form-control"  name="password-repeat" placeholder="Address" /></div>
            <div className="form-group"><input className="form-control"  name="password-repeat" placeholder="Highest Qualification" /></div> */}
            </tbody>
            )
          })}
          </table>
            <div className="form-group">
              <button className="btn btn-primary btn-block" style={{marginTop:'30px'}}  onClick={Update} /*onClick={()=>{updateProfile(val.name, val.password, val.designation, val.gender, val.address, val.highest_qualification)}}*/>Edit</button></div>
          </form>
        </div>
     
        </div>
      <script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.2.1/jquery.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/twitter-bootstrap/4.1.0/js/bootstrap.bundle.min.js"></script>
        
        </div>
    )

}
export default Profile;
