import React, {useState} from 'react';
import Axios from'axios';
import '../App.css'
import Administrator from '../components/adminDashboard';
import Popup from '../popup';



function Patients() {

    const[isOpen, setIsOpen] = useState(false)


    const [patient_list, setPatientList] = useState([])
    const [patient_name, setPatient_Name] = useState('')
    const [patient_gender, setPatient_Gender] = useState('')
    const [patient_age, setPatient_Age] = useState( )
    const [patient_address, setPatient_Address] = useState('')
    const [doctor_id, setDoctor_id] = useState( )



    const getData = ()=>{
        Axios.post('http://localhost:3001/patients_list').then((res)=>{
            setPatientList(res.data)
        })
    }


    getData();

    const delete_patient = (patient_id)=>{
        Axios.delete(`http://localhost:3001/delete_patient/${patient_id}`)
        console.log('here')
        getData();
    }

    const togglePopup = () => {
        setIsOpen(!isOpen)
        console.log(isOpen)
      }


    const insertPatient= ()=>{
        Axios.post('http://localhost:3001/insert_patient', {
            patient_name: patient_name,
            patient_gender: patient_gender,
            patient_age: patient_age,
            patient_address: patient_address
        }).then(()=>{
            alert("Inserted")
          })
          console.log('here')
          getData();
        
    }


    // const Update= (signin_id)=>{
    //     Axios.put('http://localhost:3001/admin/update_patients_list', {
    //         patient_name: patient_name,
    //         patient_gender: patient_gender,
    //         patient_age: patient_age,
    //         patient_address: patient_address,
    //         signin_id: signin_id
    //     })
    //     getData();
    // }


    return(
        <div>
        <Administrator/>
        <div className='patients' style={{marginLeft: '100px', height:'20vh'}}>
        <h1>List of Patients</h1>
        </div> 
        <div style={{marginTop:'20vh'}}> 
        <table id = 'patients' style={{alignItems: 'center',
            justifyContent: 'center', display: 'flex', height:'100vh', marginLeft: '160px',
            borderCollapse: 'separate', borderSpacing: '0 15px'}}>

      <tbody>


          <tr>
          <div style={{height:'3vh', marginBottom:'40px'}}>
        <div className="button" style={{height:'4vh',position:'absolute', right:'19%', marginTop:'10px'}}>
        <input type="button" value="Create" onClick={togglePopup} style={{ backgroundColor: '#0072c6', width:'170%', height:'150%', color:'white',
          borderRadius:'20px'}}/>
        </div>
    
    
    {isOpen && <Popup
      content={<>
      <div style={{position:'absolute', right:'50%', marginTop:'10px'}}>
        <b>Enter Data</b>
        </div>
        <form>
          <div style={{marginTop:'70px'}}>
        <label htmlFor="fname">Name:</label>
        <input type="text" id="name" name="name" style={{position:'absolute', right:'65%'}} onChange={(e)=>{setPatient_Name(e.target.value)}}/>
        <label htmlFor="fname" style={{position:'absolute', right:'55%'}}>Age:</label>
        <input type="text" id="name" name="name" style={{position:'absolute', right:'30%'}} onChange={(e)=>{setPatient_Age(e.target.value)}}/><br />
        </div>
        <div style={{marginTop:'30px'}}>
        <label htmlFor="lname">Gender:</label>
        <select  onChange={(e)=>{setPatient_Gender(e.target.value)}} style={{position:'absolute', right:'65%', width:'23%'}}>
        <option >select an option</option>
        <option value="M">M</option>
        <option value="F">F</option>
      </select>
      <label htmlFor="fname" style={{position:'absolute', right:'55%'}}>Address:</label>
        <input type="text" id="name" name="name" style={{position:'absolute', right:'30%'}} onChange={(e)=>{setPatient_Address(e.target.value)}}/>
        {/* <input type="text" id="contact" name="contact" style={{position:'absolute', right:'65%'}} onChange={(e)=>{setPatient_Gender(e.target.value)}}/> */}
        </div>
        <button type="button" style={{ backgroundColor: '#0072c6', width:'15%', height:'150%', color:'white',
          borderRadius:'20px', marginTop:'50px'}} onClick={insertPatient}>Save</button>
      </form>
      </>}
      handleClose={togglePopup}
    />}



        </div>
          </tr>

          
      {patient_list.map((val,key)=>{
        return(
          <tr style={{boxShadow: '10px 10px 5px grey', backgroundColor: '#c4d8e2',
          border:'5px', borderSpacing: '0 15px', width: '150px', textAlign:'center'}}>
            <td>{val.patient_name}
            </td>
            <td>{val.patient_gender}
            </td>
            <td>{val.patient_age}
            </td>
            <td style={{width:'30%'}}>{val.patient_address}
            </td>
            <td>
            <div className="button">
                  <button className="btn btn-primary btn-block" type="button" style={{ backgroundColor: '#0072c6', color: 'white'}}
                  onClick={()=>{delete_patient(val.patient_id)}}>Delete</button>
                </div>
            </td>
            <td>
            <div className="button">
                  <button className="btn btn-primary btn-block" type="submit" style={{ backgroundColor: '#0072c6', color: 'white'}}>Update</button>
                </div>
            </td>
            
          </tr>
        )
      })}
      </tbody>
    </table>
    </div>
        </div>
    


    )
}



export default Patients;
