import React, {useState} from 'react';
import Axios from'axios';
import '../App.css'
import Doctor from '../components/doctorDashboard'
import Popup from '../popup'

import DateMomentUtils from '@date-io/moment';
import {DatePicker, TimePicker, MuiPickersUtilsProvider} from '@material-ui/pickers'
 

function Appointments() {

  const [isOpen, setIsOpen] = useState(false)
  

    const [list, setList]=useState([])


    const [appointment_date, setAppointmentDate]=useState( )
    const [appointment_time, setAppointmentTime]=useState( )
    const [appointment_id, setAppointmentid]=useState( )
    
    const getData = () => {
        Axios.post('http://localhost:3001/appointments').then(res => {
            setList(res.data)
        })
    }
    
    getData()
  



    const togglePopup = (appointment_id, appointment_date, appointment_time) => {


      setAppointmentid(appointment_id)
      setAppointmentDate(appointment_date)
      setAppointmentTime(new Date(appointment_date).toDateString() +" "+ appointment_time +" "+ new Date().toTimeString().split(' ')[1])

      setIsOpen(!isOpen)
      console.log(isOpen)

    }


const UpdateAppointment = (appointment_date, appointment_time)=>{
  
  alert(new Date(appointment_date).toLocaleString())
  alert(new Date(appointment_time).toTimeString().split(' ')[0])

  Axios.put('http://localhost:3001/update_apointment', {
    appointment_date: new Date(appointment_date).toISOString().split('T')[0],
    appointment_time: new Date(appointment_time).toTimeString().split(' ')[0],
    appointment_id: appointment_id 
  })

  togglePopup()

  getData()

}




    return(
      <div >
        <Doctor/>
        <div className='appointments' style={{marginLeft: '100px', height:'20vh'}}>
        <h1>Your Appointments</h1>
        </div>  
        <table id = 'meetings' style={{alignItems: 'center',
            justifyContent: 'center', display: 'flex', height:'90vh', marginLeft: '160px',
            borderCollapse: 'separate', borderSpacing: '0 15px'}}>
      {/* <thead>
      <th>userName</th><th>password</th><th>status</th><th>&nbsp;</th>
      </thead> */}
      <tbody>
      {list.map((val,key)=>{
        return(
          <tr style={{boxShadow: '10px 10px 5px grey', backgroundColor: '#c4d8e2',
          border:'5px', borderSpacing: '0 15px', width: '150px', textAlign:'center'}}>
            
            
            
            <td style={{width:'50%'}}>
                {new Date(new Date().setDate(new Date(val.appointment_date).getDate())).toJSON().split('T')[0]}
              </td>


              <td>
                {(val.appointment_time).split(':').slice(0,-1).join(':')}
              </td>


            <td>{val.patient_name}
            </td>
            
            
            <td>

            <div className="button">
                  <button className="btn btn-primary btn-block" type="button" onClick={()=>{togglePopup(val.appointment_id, val.appointment_date, val.appointment_time)}} style={{ backgroundColor: '#0072c6', color: 'white'}}>Postpone</button>
                </div> 
            </td>
            
            
          </tr>



        )
      })}
      </tbody>
    </table>
    



    {isOpen && <Popup
      content={<>
      <div style={{position:'absolute', right:'50%', marginTop:'10px'}}>
        <b>Enter Data</b>
        </div>
        <div style={{ marginTop:'60px'}}>
        <MuiPickersUtilsProvider utils={DateMomentUtils} style = {{justifyContent:'center'}} >

    <div  >
    <DatePicker
          autoOk
          okLabel={false}
            label="Date"
            value={appointment_date}
            onChange={setAppointmentDate}
            format='YYYY/MM/DD'
            minDate={new Date()}
            style={{left:'15%'}}/>

      </div>      
 
    <TimePicker 
    autoOk 
    okLabel={false}
    ampm={false}
    label="Time" 
    value={appointment_time} 
    onChange={setAppointmentTime} 
    style={{left:'15%', marginTop:'25px'}}/>
    

        </MuiPickersUtilsProvider>
        
        </div>
        <button type="button" style={{ backgroundColor: '#0072c6', width:'15%', height:'150%', color:'white',
          borderRadius:'20px', marginTop:'50px'}} onClick={()=>{UpdateAppointment(appointment_date, appointment_time)}} >Save</button>
      </>}
      handleClose={togglePopup}
    />}
        </div>
    )

}
export default Appointments;
