import React, {useState} from 'react';
import Axios from'axios';
import '../App.css'
import Doctor from '../components/doctorDashboard'

function Meetings() {

    const[list, setList]=useState([])
    const [appointment_id,setappointmentid]=useState( )
    const [appointment_date,setappointmentDate]=useState( )
    const [appointment_time,setpappointmentTime]=useState( )
    const [patient_name,setPatient_Name]=useState('')
    
    const getData = () => {
        Axios.post('http://localhost:3001/meetings').then(res => {
            setList(res.data)
        })
    }
    
    getData()

    const cancel = (appointment_id)=>{
      Axios.delete(`http://localhost:3001/cancel_meeting/${appointment_id}`)
      console.log(appointment_id)
      getData();
  }
  
    return(
      <div >
        <Doctor/>
        <div className='meetings' style={{marginLeft: '100px', height:'20vh'}}>
        <h1>Upcoming Meetings</h1>
        </div>  
        <table id = 'meetings' style={{alignItems: 'center',
            justifyContent: 'center', display: 'flex', height:'90vh', marginLeft: '160px',
            borderCollapse: 'separate', borderSpacing: '0 15px', padding:'0px 20px'}}>
      {/* <thead>
      <th>userName</th><th>password</th><th>status</th><th>&nbsp;</th>
      </thead> */}
      <tbody>
      {list.map((val,key)=>{
        return(
          <tr style={{boxShadow: '10px 10px 5px grey', backgroundColor: '#c4d8e2',
          border:'10px', borderSpacing: '50px 0', width: '350px', textAlign:'center'}}>
            <td>{val.appointment_id}
            </td>


            {/* <td style={{width:'50%'}} type = {Date}>{val.appointment_date}
            </td> */}


<td style={{width:'50%'}}>
                {new Date(new Date().setDate(new Date(val.appointment_date).getDate())).toJSON().split('T')[0]}
              </td>


              <td>
                {(val.appointment_time).split(':').slice(0,-1).join(':')}
              </td>
              
            {/* <td>{val.appointment_time}
            </td> */}
            <td>{val.patient_name}
            </td>
            <td>
            <div className="button">
                  <button className="btn btn-primary btn-block" type="submit" style={{ backgroundColor: '#0072c6', color: 'white'}}>Postpone</button>
                </div>
            </td>
            <td>
            <div className="button">
                  <button className="btn btn-primary btn-block" type="submit" style={{ backgroundColor: '#0072c6', color: 'white'}} onClick={()=>{cancel(val.appointment_id)}}>Cancel</button>
                </div>
            </td>
            
          </tr>
        )
      })}
      </tbody>
    </table>
    
        </div>
    )

}
export default Meetings;
