import React, {useState} from 'react';
import Axios from'axios';
import '../App.css'
import Admin from '../components/adminDashboard';



function Patients() {

    const [patient_list, setPatientList] = useState([])
    const [patient_name, setPatient_Name] = useState('')
    const [patient_gender, setPatient_Gender] = useState('')
    const [patient_age, setPatient_Age] = useState('')
    const [patient_address, setPatient_Address] = useState('')
    const [doctor_id, setDoctor_id] = useState('')



    const getData = ()=>{
        Axios.get('http://localhost:3001/admin/patients_list').then((res)=>{
            setPatientList(res.data)
        })
    }


    getData();




    const Insert= (signin_id)=>{
        Axios.put('http://localhost:3001/admin/insert_patients_list', {
            patient_name: patient_name,
            patient_gender: patient_gender,
            patient_age: patient_age,
            patient_address: patient_address,
            doctor_id: doctor_id,
            signin_id: signin_id
        })
        getData();
    }






    const Update= (signin_id)=>{
        Axios.put('http://localhost:3001/admin/update_patients_list', {
            patient_name: patient_name,
            patient_gender: patient_gender,
            patient_age: patient_age,
            patient_address: patient_address,
            signin_id: signin_id
        })
        getData();
    }



    const Delete= (signin_id)=>{
        Axios.put('http://localhost:3001/admin/delete_patient', {
            signin_id: signin_id
        })
        getData();
    }


    return(
        <div>
            <Admin/>

            <div className='patients' style={{marginLeft: '100px', height:'20vh'}}>
                <h1>List of Patients</h1>
            </div>  
            
            <table id = 'patients' style={{alignItems: 'center', justifyContent: 'center', display: 'flex', 
                                            height:'90vh', marginLeft: '160px',borderCollapse: 'separate', 
                                            borderSpacing: '0 15px'}}>
            

            <tbody>
                {patient_list.map((val, key)=>{
                    return(
                        <tr style={{boxShadow: '10px 10px 5px grey', backgroundColor: '#c4d8e2',
                                    border:'5px', borderSpacing: '0 15px', width: '150px', 
                                    textAlign:'center'}}>              
                        

                        <td>{val.patient_name}</td>
                        <td>{val.patient_gender}</td>
                        <td>{val.patient_age}</td>
                        <td>{val.patient_address}</td>


                        <td>
                            <div>
                                <button className='btn btn-primary btn-block' onClick={Update(val.signin_id)} style={{backgroundColor: '#0072c6', color: 'white'}}>Update</button>
                            </div>
                        </td>


                        <td>
                            <div>
                                <button className='btn btn-primary btn-block' onClick={Delete(val.signin_id)} style={{backgroundColor: '#0072c6', color: 'white'}}>Delete</button>
                            </div>
                        </td>
                        
                        </tr>

                    
                    )
                })}

            </tbody>

            </table>
    
        </div>
    


    )
}



export default Patients;
