import React, {useState} from 'react';
import Axios from'axios';
import '../App.css'
import Administrator from '../components/adminDashboard';
import Popup from '../popup';



function Doctors() {

    const[isOpen, setIsOpen] = useState(false)
    const [doctor_list, setDoctorList] = useState([])
    const [doctor_id, setdoctorid] = useState( )
    const [name, setName] = useState('')
    const [designation, setDesignation] = useState('')
    const [gender, setGender] = useState('')
    const [address, setAddress] = useState('')
    const [highest_qualification, setHighest_Qualification] = useState('')



    const getData = ()=>{
        Axios.post('http://localhost:3001/doctors_list').then((res)=>{
            setDoctorList(res.data)
        })
    }


    getData();

    const delete_doctor = (doctor_id)=>{
        Axios.delete(`http://localhost:3001/delete_doctor/${doctor_id}`)
        console.log('here')
        getData();
    }

    const togglePopup = () => {
        setIsOpen(!isOpen)
        console.log(isOpen)
      }


    const insertDoctor= ()=>{
        Axios.post('http://localhost:3001/insert_doctor', {
            name: name,
            designation: designation,
            gender: gender,
            address: address,
            highest_qualification: highest_qualification,
        }).then(()=>{
            alert("Inserted")
          })
          console.log('here')
          getData();
        
    }

    // const Update= (signin_id)=>{
    //     Axios.put('http://localhost:3001/admin/update_doctors_list', {
    //         name: name,
    //         designation: designation,
    //         gender: gender,
    //         address: address,
    //         highest_qualification: highest_qualification,
    //         signin_id: signin_id
    //     })
    //     getData();
    // }



    return(
        <div >
        <Administrator/>
        <div className='patients' style={{marginLeft: '100px', height:'20vh'}}>
        <h1>List of Doctors</h1>
        </div>  
        <table id = 'patients' style={{alignItems: 'center',
            justifyContent: 'center', display: 'flex', height:'20vh', marginLeft: '160px',
            borderCollapse: 'separate', borderSpacing: '0 15px'}}>
      <tbody>
      <tr>
          <div style={{height:'3vh', marginBottom:'40px'}}>
        <div className="button" style={{height:'4vh',position:'absolute', right:'26%', marginTop:'10px'}}>
        <input type="button" value="Create" onClick={togglePopup} style={{ backgroundColor: '#0072c6', width:'170%', height:'150%', color:'white',
          borderRadius:'20px'}}/>
        </div>
    {isOpen && <Popup
      content={<>
      <div style={{position:'absolute', right:'50%', marginTop:'10px'}}>
        <b>Enter Data</b>
        </div>
        <form>
          <div style={{marginTop:'70px'}}>
        <label htmlFor="fname">Name:</label>
        <input type="text" id="name" name="name" style={{position:'absolute', right:'65%'}} onChange={(e)=>{setName(e.target.value)}}/>
        <label htmlFor="fname" style={{position:'absolute', right:'51%'}}>Designation:</label>
        <input type="text" id="name" name="name" style={{position:'absolute', right:'27%'}} onChange={(e)=>{setDesignation(e.target.value)}}/><br />
        </div>
        <div style={{marginTop:'30px'}}>
        <label htmlFor="lname">Gender:</label>
        <select  onChange={(e)=>{setGender(e.target.value)}} style={{position:'absolute', right:'65%', width:'23%'}}>
        <option value="M">M</option>
        <option value="F">F</option>
      </select>
      <label htmlFor="fname" style={{position:'absolute', right:'55%'}}>Address:</label>
        <input type="text" id="name" name="name" style={{position:'absolute', right:'27%'}} onChange={(e)=>{setAddress(e.target.value)}}/>
        {/* <input type="text" id="contact" name="contact" style={{position:'absolute', right:'65%'}} onChange={(e)=>{setPatient_Gender(e.target.value)}}/> */}
        </div>
        <button style={{ backgroundColor: '#0072c6', width:'15%', height:'150%', color:'white',
          borderRadius:'20px', marginTop:'50px'}} onClick={insertDoctor}>Save</button>
      </form>
      </>}
      handleClose={togglePopup}
    />}
        </div>
          </tr>
      {doctor_list.map((val,key)=>{
        return(
          <tr style={{boxShadow: '10px 10px 5px grey', backgroundColor: '#c4d8e2',
          border:'5px', borderSpacing: '0 15px', width: '150px', textAlign:'center'}}>
            <td>{val.name}
            </td>
            <td>{val.designation}
            </td>
            <td>{val.gender}
            </td>
            <td style={{width:'20%'}}>{val.address}
            </td>
            <td>{val.highest_qualification}
            </td>
            <td>
            <div className="button">
                  <button className="btn btn-primary btn-block" type="button" style={{ backgroundColor: '#0072c6', color: 'white'}}
                  onClick={()=>{delete_doctor(val.doctor_id)}}>Delete</button>
                </div>
            </td>
            <td>
            <div className="button">
                  <button className="btn btn-primary btn-block" type="submit" style={{ backgroundColor: '#0072c6', color: 'white'}}>Update</button>
                </div>
            </td>
          </tr>
        )
      })}
      </tbody>
    </table>
    
        </div>
    


    )
}



export default Doctors;
